mapper.update:
UPDATE students
SET nickname = #{nickname}, name = #{name}, email = #{email, jdbcType=VARCHAR}, comments = #{comments, jdbcType=VARCHAR}
WHERE id = #{id}

UPDATE tasks
SET confirmed = #{confirmed}, title = #{title}, description = #{description, jdbcType=VARCHAR},
	schedule_id = #{schedule_id, jdbcType=NUMERIC}
WHERE id = #{id}
