mapper.update:
UPDATE students
SET nickname = #{nickname}, name = #{name}, email = #{email, jdbcType=VARCHAR}, 
comments = #{comments, jdbcType=VARCHAR}, rate = #{rate, jdbcType=NUMERIC}
WHERE id = #{id}

UPDATE tasks
SET confirmed = #{confirmed}, title = #{title}, description = #{description, jdbcType=VARCHAR},
	schedule_id = #{schedule_id, jdbcType=NUMERIC}
WHERE id = #{id}

UPDATE payments
SET rate = #{rate, jdbcType=NUMERIC}
WHERE student_id = #{id, jdbcType=NUMERIC}
AND paid = 0
